/**
 * 円周率の小数点以下10,000桁
 * 3.14159265358979323846...
 */

// 円周率の小数点以下（最初の1000桁）
export const PI_DIGITS = "";

/**
 * 指定された位置の数字を取得
 * @param position 0から始まる位置（0 = 小数点以下1桁目）
 * @returns その位置の数字（0-9の文字列）
 */
export function getDigitAt(position: number): string {
  if (position < 0 || position >= PI_DIGITS.length) {
    throw new Error(`Position ${position} is out of range`);
  }
  return PI_DIGITS[position];
}

/**
 * 入力された数字が正しいかを検証
 * @param position 0から始まる位置
 * @param input 入力された数字（0-9の文字列）
 * @returns 正しければtrue、そうでなければfalse
 */
export function validateDigit(position: number, input: string): boolean {
  if (position < 0 || position >= PI_DIGITS.length) {
    return false;
  }
  return PI_DIGITS[position] === input;
}

/**
 * 指定範囲の桁を取得
 * @param start 開始位置（0から始まる）
 * @param end 終了位置（含まない）
 * @returns 指定範囲の数字列
 */
export function getDigits(start: number, end: number): string {
  return PI_DIGITS.slice(start, end);
}

/**
 * 利用可能な最大桁数を取得
 * @returns 最大桁数
 */
export function getMaxDigits(): number {
  return PI_DIGITS.length;
}

/**
 * 円周率の表示用文字列を生成（"3.14159..."）
 * @param digits 小数点以下の桁数
 * @returns 表示用文字列
 */
export function formatPi(digits: number): string {
  return `3.${getDigits(0, Math.min(digits, PI_DIGITS.length))}`;
}
